args.new_str:
function translatePage(lang) {
    // Translate section headings
    document.querySelector('#about h2').textContent = translations[lang].aboutMe;
    document.querySelector('#experience h2').textContent = translations[lang].experience;
    document.querySelector('#certifications h2').textContent = translations[lang].certifications;
    document.querySelector('#projects h2').textContent = translations[lang].projects;

    // Translate tagline
    document.querySelector('.tagline').textContent = translations[lang].tagline;

    // Translate about section
    const aboutParagraphs = document.querySelectorAll('.about-text p');
    aboutParagraphs[0].textContent = translations[lang].aboutText1;
    aboutParagraphs[1].textContent = translations[lang].aboutText2;

    // Handle the third paragraph with the ITESO link
    const itesoLink = aboutParagraphs[2].querySelector('a');
    aboutParagraphs[2].innerHTML = translations[lang].aboutText3;
    aboutParagraphs[2].innerHTML = aboutParagraphs[2].innerHTML.replace('ITESO', `<a href="https://iteso.mx/" target="_blank">ITESO</a>`);

    // Handle the fourth paragraph with Discord and Medium links
    const discordLink = aboutParagraphs[3].querySelector('a[href*="discord"]');
    const mediumLink = aboutParagraphs[3].querySelector('a[href*="medium"]');
    aboutParagraphs[3].innerHTML = translations[lang].aboutText4;

    // Translate experience section
    document.querySelector('.experience .timeline-item:nth-child(1) h3').textContent = translations[lang].devopsConsultant;
    document.querySelector('.experience .timeline-item:nth-child(1) .date').textContent = translations[lang].devopsConsultantDate;
    document.querySelector('.experience .timeline-item:nth-child(1) p').textContent = translations[lang].devopsConsultantDesc;

    document.querySelector('.experience .timeline-item:nth-child(2) h3').textContent = translations[lang].sreWizeline;
    document.querySelector('.experience .timeline-item:nth-child(2) .date').textContent = translations[lang].sreWizelineDate;
    document.querySelector('.experience .timeline-item:nth-child(2) p').textContent = translations[lang].sreWizelineDesc;

    document.querySelector('.experience .timeline-item:nth-child(3) h3').textContent = translations[lang].linuxRackspace;
    document.querySelector('.experience .timeline-item:nth-child(3) .date').textContent = translations[lang].linuxRackspaceDate;
    document.querySelector('.experience .timeline-item:nth-child(3) p').textContent = translations[lang].linuxRackspaceDesc;

    document.querySelector('.experience .timeline-item:nth-child(4) h3').textContent = translations[lang].devopsOracle;
    document.querySelector('.experience .timeline-item:nth-child(4) .date').textContent = translations[lang].devopsOracleDate;
    document.querySelector('.experience .timeline-item:nth-child(4) p').textContent = translations[lang].devopsOracleDesc;

    // Translate education section
    document.querySelector('.education .timeline-item:nth-child(1) h3').textContent = translations[lang].professorIteso;
    document.querySelector('.education .timeline-item:nth-child(1) .date').textContent = translations[lang].professorItesoDate;
    document.querySelector('.education .timeline-item:nth-child(1) p').textContent = translations[lang].professorItesoDesc;

    document.querySelector('.education .timeline-item:nth-child(2) h3').textContent = translations[lang].mastersDegree;
    document.querySelector('.education .timeline-item:nth-child(2) .date').textContent = translations[lang].mastersSchool;
    document.querySelector('.education .timeline-item:nth-child(2) p').textContent = translations[lang].mastersDesc;

    document.querySelector('.education .timeline-item:nth-child(3) h3').textContent = translations[lang].bachelorDegree;
    document.querySelector('.education .timeline-item:nth-child(3) .date').textContent = translations[lang].bachelorSchool;
    document.querySelector('.education .timeline-item:nth-child(3) p').textContent = translations[lang].bachelorDesc;

    // Translate projects section
    document.querySelector('.projects-grid .project-card:nth-child(1) .project-title').textContent = translations[lang].foundationalLabs;
    document.querySelector('.projects-grid .project-card:nth-child(1) p').textContent = translations[lang].foundationalLabsDesc;
    document.querySelector('.projects-grid .project-card:nth-child(1) .project-link').textContent = translations[lang].exploreLabs;

    document.querySelector('.projects-grid .project-card:nth-child(2) .project-title').textContent = translations[lang].intermediateLabs;
    document.querySelector('.projects-grid .project-card:nth-child(2) p').textContent = translations[lang].intermediateLabsDesc;
    document.querySelector('.projects-grid .project-card:nth-child(2) .project-link').textContent = translations[lang].exploreLabs;

    document.querySelector('.projects-grid .project-card:nth-child(3) .project-title').textContent = translations[lang].advancedProjects;
    document.querySelector('.projects-grid .project-card:nth-child(3) p').textContent = translations[lang].advancedProjectsDesc;
    document.querySelector('.projects-grid .project-card:nth-child(3) .project-link').textContent = translations[lang].exploreProjects;

    document.querySelector('.projects-grid .project-card:nth-child(4) .project-title').textContent = translations[lang].expertProjects;
    document.querySelector('.projects-grid .project-card:nth-child(4) p').textContent = translations[lang].expertProjectsDesc;
    document.querySelector('.projects-grid .project-card:nth-child(4) .project-link').textContent = translations[lang].exploreProjects;

    // Translate buttons
    const buttons = document.querySelectorAll('header .cta-button');
    buttons[0].textContent = translations[lang].viewResume;
    buttons[1].textContent = translations[lang].exploreLabs;

    // Translate footer
    document.querySelector('footer p:first-child').textContent = translations[lang].copyright;
    document.querySelector('.disclaimer').textContent = translations[lang].disclaimer;
    document.querySelector('.amazon-q-credit').innerHTML = translations[lang].amazonQCredit + ' <a href="https://aws.amazon.com/q/" target="_blank">Amazon Q</a>';
    document.querySelector('.carpe-diem').textContent = translations[lang].carpeDiem;
}

