args.new_str:
/* Language selector */
.language-selector {
    position: fixed;
    top: 20px;
    left: 20px;
    z-index: 1000;
    display: flex;
    gap: 10px;
}

.language-btn {
    background: rgba(0,0,0,0.2);
    border: none;
    border-radius: 4px;
    padding: 5px 10px;
    cursor: pointer;
    color: white;
    transition: background-color 0.3s;
    font-weight: bold;
}

body.dark-mode .language-btn {
    background: rgba(255,255,255,0.2);
    color: white;
}

.language-btn:hover,
.language-btn.active {
    background-color: var(--secondary-color);
    color: white;
}

body.dark-mode .language-btn.active {
    background-color: var(--secondary-color);
    color: white;
}

