args.file_text:
/* Main styles for Alex Garcia's website */
:root {
    --primary-color: #0066cc;
    --secondary-color: #ff9900;
    --dark-color: #232f3e;
    --light-color: #f8f9fa;
    --text-color: #333;
    --link-color: #0066cc;
    --visited-link-color: #551A8B;

    /* Dark mode variables - OneDark theme */
    --dark-mode-bg: #282c34;
    --dark-mode-text: #abb2bf;
    --dark-mode-card-bg: #21252b;
    --dark-mode-secondary: #e5c07b;
    --dark-mode-link: #61afef;
    --dark-mode-visited-link: #c678dd;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: var(--text-color);
    background-color: var(--light-color);
    transition: background-color 0.3s, color 0.3s;
}

/* Dark mode styles */
body.dark-mode {
    color: var(--dark-mode-text);
    background-color: var(--dark-mode-bg);
}

body.dark-mode .project-card,
body.dark-mode .project-tag,
body.dark-mode .access-form {
    background-color: var(--dark-mode-card-bg);
    color: var(--dark-mode-text);
}

body.dark-mode h2,
body.dark-mode .project-title {
    color: var(--dark-mode-text);
}

body.dark-mode .timeline-item .date {
    color: #aaa;
}

/* Dark mode link color adjustment for better contrast - only for text links */
body.dark-mode a:not(.cta-button):not(.social-links a):not(.language-btn),
body.dark-mode .project-link {
    color: var(--dark-mode-link);
}

body.dark-mode a:not(.cta-button):not(.social-links a):not(.language-btn):visited,
body.dark-mode .project-link:visited {
    color: var(--dark-mode-visited-link);
}

/* Theme toggle */
.theme-toggle {
    position: fixed;
    top: 20px;
    right: 20px;
    z-index: 1000;
    background: rgba(0,0,0,0.1);
    border: none;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
    color: var(--text-color);
    transition: background-color 0.3s;
}

body.dark-mode .theme-toggle {
    background: rgba(255,255,255,0.1);
    color: var(--dark-mode-text);
}

.theme-toggle:hover {
    background: rgba(0,0,0,0.2);
}

body.dark-mode .theme-toggle:hover {
    background: rgba(255,255,255,0.2);
}

/* Language selector */
.language-selector {
    position: fixed;
    top: 20px;
    left: 20px;
    z-index: 1000;
    display: flex;
    gap: 10px;
}

.language-btn {
    background: rgba(0,0,0,0.2);
    border: none;
    border-radius: 4px;
    padding: 5px 10px;
    cursor: pointer;
    color: white;
    transition: background-color 0.3s;
    font-weight: bold;
}

body.dark-mode .language-btn {
    background: rgba(255,255,255,0.2);
    color: white;
}

.language-btn:hover,
.language-btn.active {
    background-color: var(--secondary-color);
    color: white;
}

body.dark-mode .language-btn.active {
    background-color: var(--secondary-color);
    color: white;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

header {
    background-color: var(--dark-color);
    color: white;
    padding: 60px 0;
    text-align: center;
}

.profile-img {
    width: 150px;
    height: 150px;
    border-radius: 50%;
    object-fit: cover;
    border: 5px solid var(--secondary-color);
    margin-bottom: 20px;
}

h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.tagline {
    font-size: 1.2rem;
    margin-bottom: 20px;
    opacity: 0.9;
}

.social-links {
    margin: 20px 0;
}

