// Form submission handler
function sendAccessRequest(event) {
    event.preventDefault();

    const form = document.getElementById('access-form');
    const successMessage = document.getElementById('success-message');

    // Get form data
    const name = document.getElementById('name').value;
    const email = document.getElementById('email').value;
    const github = document.getElementById('github').value;
    const repo = document.getElementById('repo').value;
    const reason = document.getElementById('reason').value;

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
        alert('Please enter a valid email address');
        return false;
    }

    // Validate GitHub username (check if it exists)
    validateGitHubUsername(github).then(isValid => {
        if (!isValid) {
            alert('GitHub username does not exist. Please enter a valid username.');
            return;
        }

        // Create GitHub issue for access request
        createGitHubIssue(name, email, github, repo, reason).then(issueUrl => {
            // Show success message
            form.style.display = 'none';
            successMessage.style.display = 'block';

            // Hide error message
            document.getElementById('error-message').style.display = 'none';

            // Send notification email
            const subject = `Repository Access Request: ${repo}`;
            const body = `Name: ${name}%0D%0AEmail: ${email}%0D%0AGitHub Username: ${github}%0D%0ARepository: ${repo}%0D%0AReason: ${reason}%0D%0A%0D%0AAn issue has been created: ${issueUrl}`;
            const mailtoLink = `mailto:alejandrogarcia@iteso.mx?subject=${encodeURIComponent(subject)}&body=${body}`;

            // Open email client
            window.location.href = mailtoLink;
        }).catch(error => {
            console.error('Error creating GitHub issue:', error);
            alert('There was an error submitting your request. Please try again later.');
        });
    });

    return false;
}

// Function to validate GitHub username
async function validateGitHubUsername(username) {
    try {
        const response = await fetch(`https://api.github.com/users/${username}`);
        return response.status === 200;
    } catch (error) {
        console.error('Error validating GitHub username:', error);
        return true; // Return true on error to allow form submission
    }
}

// Function to create GitHub issue for access request
async function createGitHubIssue(name, email, github, repo, reason) {
    // In a real implementation, this would make an API call to create a GitHub issue
    // For now, we'll simulate it and return a fake issue URL

    // This would normally be done through a server-side API to keep the token secure
    // For demonstration purposes, we're simulating the response

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Return fake issue URL
    return `https://github.com/gamaware/cloud-devops-labs-index/issues/new`;
}

